export function formatDate(d){
  if(!d) return '-';
  const o = new Date(d + 'T00:00:00');
  if(isNaN(o)) return d;
  return o.toLocaleDateString('th-TH',{ day:'2-digit', month:'short', year:'numeric'});
}

export function timeRange(s,e){
  if(!s && !e) return '-';
  return [s||'', e?(' - '+e):''].join('');
}
