import React, { useState, useRef, useLayoutEffect, useCallback } from 'react';
import PropTypes from 'prop-types';

function RowItem({
  item,
  isOpen = false,
  onToggle = () => {},     // ✅ default no-op
  activeTab,
  onComplete = () => {},   // ✅ default no-op
  onPostpone = () => {},   // ✅ default no-op
}) {
  const [ripples, setRipples] = useState([]);
  const wrapRef = useRef(null);
  const [maxH, setMaxH] = useState(0);

  useLayoutEffect(() => {
    if (wrapRef.current) {
      const h = wrapRef.current.scrollHeight || 0;
      setMaxH(isOpen ? h : 0);
    }
  }, [isOpen, item?.planningid]); // ลดการคำนวณซ้ำ

  const ripple = useCallback((e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    const id = Date.now() + Math.random();
    setRipples(r => [...r, { id, x, y }]);
    setTimeout(() => setRipples(r => r.filter(i => i.id !== id)), 600);
  }, []);

  const handleRowClick = useCallback(() => {
    if (typeof onToggle === 'function') {
      onToggle(item.planningid);
    } else {
      console.warn('onToggle is not a function', { onToggle, item });
    }
  }, [onToggle, item?.planningid]);

  const handleComplete = useCallback(() => {
    if (typeof onComplete === 'function') {
      onComplete(item.planningid);
    } else {
      console.warn('onComplete is not a function', { onComplete, item });
    }
  }, [onComplete, item?.planningid]);

  const handlePostpone = useCallback(() => {
    if (typeof onPostpone === 'function') {
      onPostpone(item.planningid);
    } else {
      console.warn('onPostpone is not a function', { onPostpone, item });
    }
  }, [onPostpone, item?.planningid]);

  return (
    <div className="row-wrap">
      <div
        className={`row ${isOpen ? 'is-open' : ''}`}
        onMouseDown={ripple}
        onClick={handleRowClick}
      >
        {ripples.map(r => (
          <span key={r.id} className="ripple" style={{ left: r.x, top: r.y }} />
        ))}

        <div className="cell cell-project">
          <div className="title-project">{item.projectname}</div>
        </div>

        <div className="cell cell-account">
          <div className="text-account">{item.accountname}</div>
        </div>

        <div className="cell cell-meta">
          {activeTab === 'complete'
            ? <span className="badge-success">Success</span>
            : <span className="date-text">{item.dateStart || '-'}</span>}
        </div>
      </div>

      <div
        className={`detail-anim ${isOpen ? 'open' : ''}`}
        style={{ maxHeight: isOpen ? `${maxH}px` : 0 }}
      >
        <div className="detail" ref={wrapRef} onClick={(e) => e.stopPropagation()}>
          <div className="detail-box">
            <div className="stack">
              <div className="kv"><b>Engineer:</b> {item.engineer || '-'}</div>
              <div className="kv"><b>Start Date:</b> {item.dateStart || '-'}</div>
              <div className="kv"><b>End Date:</b> {item.dateEnd || '-'}</div>
              <div className="kv"><b>Time Start:</b> {item.timeStart || '-'}</div>
              <div className="kv"><b>Time End:</b> {item.timeEnd || '-'}</div>
              {activeTab === 'complete' && (
                <div className="kv"><b>Complete Date:</b> {item.dateSuccess || '-'}</div>
              )}
            </div>

            {activeTab === 'pending' && (
              <div className="btns">
                <button className="btn btn-success" onClick={handleComplete}>Complete</button>
                <button className="btn btn-warn" onClick={handlePostpone}>Postpone</button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

RowItem.propTypes = {
  item: PropTypes.shape({
    planningid: PropTypes.oneOfType([PropTypes.string, PropTypes.number]).isRequired,
    projectname: PropTypes.string,
    accountname: PropTypes.string,
    dateStart: PropTypes.string,
    dateEnd: PropTypes.string,
    timeStart: PropTypes.string,
    timeEnd: PropTypes.string,
    dateSuccess: PropTypes.string,
    engineer: PropTypes.string,
  }).isRequired,
  isOpen: PropTypes.bool,
  onToggle: PropTypes.func,
  activeTab: PropTypes.oneOf(['upcoming', 'pending', 'complete']).isRequired,
  onComplete: PropTypes.func,
  onPostpone: PropTypes.func,
};

export default RowItem;
