import React, { useEffect, useMemo, useState, useCallback } from 'react';
import RowItem from './components/RowItem';
import mockData from './api/mockData';
import './styles/index.css';

function App() {
  const [activeTab, setActiveTab] = useState('upcoming'); // 'upcoming' | 'pending' | 'complete'
  const [rows, setRows] = useState(mockData);
  const [search, setSearch] = useState('');
  const [openId, setOpenId] = useState(null); // แถวที่เปิดอยู่

  // กำหนดวิธีจับคู่สถานะ → แท็บ
  const tabMatchers = {
    upcoming: (s) =>
      ['scheduled', 'planning', 'upcoming'].includes((s || '').toLowerCase()),
    pending: (s) =>
      ['wait success', 'pending'].includes((s || '').toLowerCase()),
    complete: (s) =>
      ['success', 'completed', 'complete'].includes((s || '').toLowerCase()),
  };

  // กรองตามแท็บ
  const tabFilteredRows = useMemo(() => {
    const match = tabMatchers[activeTab];
    if (!rows?.length) return [];
    if (rows[0].isStatus == null) return rows; // mock ยังไม่มีสถานะ → แสดงทั้งหมด
    return rows.filter((r) => match(r.isStatus));
  }, [rows, activeTab]);

  // ค้นหา
  const filteredRows = useMemo(() => {
    const term = search.toLowerCase().trim();
    if (!term) return tabFilteredRows;
    return tabFilteredRows.filter((row) =>
      (row.projectname || '').toLowerCase().includes(term) ||
      (row.accountname || '').toLowerCase().includes(term)
    );
  }, [tabFilteredRows, search]);

  const title =
    activeTab === 'upcoming'
      ? 'Upcoming Projects'
      : activeTab === 'pending'
      ? 'Pending Projects'
      : 'Completed Projects';

  // ฟังก์ชัน toggle ส่งให้ RowItem
  const handleToggle = useCallback((id) => {
    setOpenId((prev) => (prev === id ? null : id));
  }, []);

  // ตัวอย่างอัปเดตสถานะ (ปกติเรียก API PHP)
  const handleComplete = useCallback((id) => {
    setRows((prev) =>
      prev.map((it) =>
        it.planningid === id
          ? {
              ...it,
              isStatus: 'success',
              dateSuccess: new Date().toISOString().slice(0, 10), // YYYY-MM-DD
            }
          : it
      )
    );
  }, []);

  const handlePostpone = useCallback((id) => {
    setRows((prev) =>
      prev.map((it) => {
        if (it.planningid !== id) return it;
        const d = it.dateStart ? new Date(it.dateStart) : new Date();
        d.setDate(d.getDate() + 7);
        return {
          ...it,
          isStatus: 'pending',
          dateStart: d.toISOString().slice(0, 10),
        };
      })
    );
  }, []);

  // เปลี่ยนแท็บให้ปิด accordion เดิม
  useEffect(() => {
    setOpenId(null);
  }, [activeTab]);

  return (
    <div className="page">
      <div className="header">
        <h2 className="title">{title}</h2>
        <div className="search-wrap">
          <input
            className="search-input"
            placeholder="Search Project or Account"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </div>
      </div>

      <div className="card">
        <div className="table" role="table" aria-label="Projects">
          <div className="thead" role="row">
            <div className="cell" role="columnheader">Project Name</div>
            <div className="cell" role="columnheader">Account Name</div>
            <div className="cell cell-meta" role="columnheader">
              {activeTab === 'complete' ? 'Status' : 'Start Date'}
            </div>
          </div>

          {filteredRows.length === 0 && (
            <div className="row state">No data available</div>
          )}

          {filteredRows.map((item) => (
            <RowItem
              key={item.planningid}
              item={item}
              isOpen={openId === item.planningid}
              activeTab={activeTab}
              onToggle={handleToggle}
              onComplete={handleComplete}
              onPostpone={handlePostpone}
            />
          ))}
        </div>
      </div>

      <div className="footer">
        <button
          className={activeTab === 'upcoming' ? 'active' : ''}
          onClick={() => setActiveTab('upcoming')}
        >
          Upcoming
        </button>
        <button
          className={activeTab === 'pending' ? 'active' : ''}
          onClick={() => setActiveTab('pending')}
        >
          Pending
        </button>
        <button
          className={activeTab === 'complete' ? 'active' : ''}
          onClick={() => setActiveTab('complete')}
        >
          Complete
        </button>
      </div>
    </div>
  );
}

export default App;
