import { useState, useEffect, useMemo } from 'react';
import { Icon } from './components/Icon.jsx';
import { RowItem } from './components/RowItem.jsx';
import { mockData } from './data/mockData.js';

const USE_MOCK = true;
const API_BASE = '/pm1/api';

export default function App(){
  const [activeTab, setActiveTab] = useState('upcoming');
  const [data, setData] = useState([]);
  const [openId, setOpenId] = useState(null);
  const [loading, setLoading] = useState(false);
  const [q, setQ] = useState('');
  const [error, setError] = useState('');

  useEffect(()=>{
    let abort = false;
    async function load(){
      setLoading(true);
      setError('');
      try{
        let rows;
        if(USE_MOCK){
          await new Promise(r=>setTimeout(r,150));
          rows = mockData[activeTab] || [];
        }else{
          const fd = new FormData();
          fd.append('type', activeTab);
            const res = await fetch(`${API_BASE}/getProjects.php`, {method:'POST', body:fd});
          if(!res.ok) throw new Error(res.status+' '+res.statusText);
          rows = await res.json();
        }
        if(!abort){
          setData(rows);
          setOpenId(null);
        }
      }catch(e){
        if(!abort) setError(e.message);
      }finally{
        !abort && setLoading(false);
      }
    }
    load();
    return ()=>{ abort = true; };
  },[activeTab]);

  const filtered = useMemo(()=>{
    if(!q.trim()) return data;
    const k = q.toLowerCase();
    return data.filter(d=>
      (d.projectname||'').toLowerCase().includes(k) ||
      (d.accountname||'').toLowerCase().includes(k) ||
      (d.engineers||'').toLowerCase().includes(k)
    );
  },[q, data]);

  return (
    <div className="page">
      <header className="header">
        <div>
          <h1 className="title">Projects PM</h1>
          <p className="subtitle">แสดงข้อมูลตามหมวดหมู่: upcoming / pending / complete</p>
        </div>
      </header>

      <div className="card">
        <div className="search-wrap">
          <span className="search-icon"><Icon.Search /></span>
          <input
            className="search-input"
            placeholder="ค้นหาโครงการ / ลูกค้า / วิศวกร..."
            value={q}
            onChange={e=>setQ(e.target.value)}
          />
        </div>

        <div className="table">
          <div className="thead">
            <div>Project / Account</div>
            <div>วันที่</div>
            <div>{activeTab==='complete' ? 'สถานะ' : 'เวลา'}</div>
          </div>

          {loading && (
            <div className="state">
              <div className="spinner" /> กำลังโหลด...
            </div>
          )}

          {error && !loading && (
            <div className="state" style={{color:'crimson'}}>
              เกิดข้อผิดพลาด: {error}
            </div>
          )}

          {!loading && !error && filtered.length===0 && (
            <div className="state">ไม่พบข้อมูล</div>
          )}

          {!loading && !error && filtered.map(item=>(
            <RowItem
              key={item.planningid}
              item={item}
              open={openId === item.planningid}
              activeTab={activeTab}
              onToggle={()=> setOpenId(o=> o===item.planningid ? null : item.planningid)}
            />
          ))}
        </div>
      </div>

      <footer className="footer">
        <div className="footer-inner">
          <button
            className={"pill"+(activeTab==='upcoming'?' active':'')}
            onClick={()=>setActiveTab('upcoming')}
          >
            <Icon.Calendar />
            <small>Upcoming</small>
          </button>
          <button
            className={"pill"+(activeTab==='pending'?' active':'')}
            onClick={()=>setActiveTab('pending')}
          >
            <Icon.Clock />
            <small>Pending</small>
          </button>
            <button
            className={"pill"+(activeTab==='complete'?' active':'')}
            onClick={()=>setActiveTab('complete')}
          >
            <Icon.Check />
            <small>Complete</small>
          </button>
        </div>
      </footer>
    </div>
  );
}
