import { useState, useMemo } from 'react';
import { Icon } from './components/Icon.jsx';
import { RowItem } from './components/RowItem.jsx';
import { mockData } from './data/mockData.js';

const TAB_TITLES = {
  upcoming: 'Upcoming Projects',
  pending: 'Pending Projects',
  complete: 'Completed Projects'
};

function todayISO(){
  const d = new Date();
  return d.toISOString().slice(0,10);
}

export default function App(){
  // clone mock (ไม่แก้ต้นฉบับ)
  const [lists, setLists] = useState(()=> JSON.parse(JSON.stringify(mockData)));
  const [activeTab, setActiveTab] = useState('pending'); // เริ่มให้เห็นปุ่มก่อน
  const [openId, setOpenId] = useState(null);
  const [q, setQ] = useState('');
  const [toast, setToast] = useState(null);

  const data = lists[activeTab] || [];

  const filtered = useMemo(()=>{
    if(!q.trim()) return data;
    const k = q.toLowerCase();
    return data.filter(d =>
      (d.projectname||'').toLowerCase().includes(k) ||
      (d.accountname||'').toLowerCase().includes(k) ||
      (d.engineers||'').toLowerCase().includes(k)
    );
  },[q, data]);

  function showToast(msg){
    setToast(msg);
    setTimeout(()=> setToast(null), 2400);
  }

  function handleComplete(id){
    if(!window.confirm('ยืนยัน Complete รายการนี้?')) return;
    setLists(prev=>{
      const pending = [...prev.pending];
      const idx = pending.findIndex(i=>i.planningid===id);
      if(idx === -1) return prev;
      const item = {...pending[idx], isStatus:'success', dateSuccess: todayISO()};
      pending.splice(idx,1);
      const complete = [...prev.complete, item];
      return {...prev, pending, complete};
    });
    setOpenId(null);
    showToast('Moved to Complete');
    setActiveTab('complete'); // ย้ายไปดูแท็บ Complete ทันที
  }

  function handlePostpone(id){
    if(!window.confirm('ยืนยัน Postpone (เอาออกจาก Pending)?')) return;
    setLists(prev=>{
      const pending = prev.pending.filter(i=> i.planningid !== id);
      return {...prev, pending};
    });
    setOpenId(null);
    showToast('Postponed (removed)');
    // คงอยู่ที่ pending ให้ผู้ใช้ทำต่อ
  }

  return (
    <div className="page">
      <header className="header">
        <div>
          <h1 className="title">{TAB_TITLES[activeTab]}</h1>
          <p className="subtitle">{todayISO()}</p>
        </div>
      </header>

      <div className="card">
        <div className="search-wrap">
          <span className="search-icon"><Icon.Search /></span>
          <input
            className="search-input"
            placeholder="ค้นหา project / account / engineer..."
            value={q}
            onChange={e=>setQ(e.target.value)}
          />
        </div>

        <div className="table">
          <div className="thead">
            <div>Project Name</div>
            <div>Account Name</div>
            <div>{activeTab==='complete' ? 'Status' : 'Start Date'}</div>
          </div>

          {filtered.length === 0 && (
            <div className="state">ไม่พบข้อมูล</div>
          )}

          {filtered.map(item=>(

            <RowItem
              key={item.planningid}
              item={item}
              open={openId === item.planningid}
              activeTab={activeTab}
              onToggle={()=> setOpenId(o=> o===item.planningid ? null : item.planningid)}
              onComplete={()=>handleComplete(item.planningid)}
              onPostpone={()=>handlePostpone(item.planningid)}
            />
          ))}
        </div>
      </div>

      <footer className="footer">
        <div className="footer-inner">
          <button className={"pill"+(activeTab==='upcoming'?' active':'')} onClick={()=>{setActiveTab('upcoming');setOpenId(null);}}>
            <Icon.Calendar /><small>Upcoming</small>
          </button>
          <button className={"pill"+(activeTab==='pending'?' active':'')} onClick={()=>{setActiveTab('pending');setOpenId(null);}}>
            <Icon.Clock /><small>Pending</small>
          </button>
          <button className={"pill"+(activeTab==='complete'?' active':'')} onClick={()=>{setActiveTab('complete');setOpenId(null);}}>
            <Icon.Check /><small>Complete</small>
          </button>
        </div>
      </footer>

      {toast && (
        <div className="toast">{toast}</div>
      )}
    </div>
  );
}
