import { useState, useEffect, useRef, useLayoutEffect } from 'react';
import { formatDate, timeRange } from '../utils/format.js';

function StatusBadge({ status }){
  if(!status) return null;
  const s = status.toLowerCase();
  if(s.includes('success')) return <span className="badge-success">Success</span>;
  if(s.includes('wait')) return <span className="badge-wait">Waiting</span>;
  if(s.includes('schedule')) return <span className="badge-scheduled">Scheduled</span>;
  return <span className="badge-wait">{status}</span>;
}

export function RowItem({ item, open, onToggle, activeTab }){
  const [ripples, setRipples] = useState([]);
  const detailRef = useRef(null);
  const [maxH, setMaxH] = useState(0);

  useLayoutEffect(()=>{
    if(open && detailRef.current){
      setMaxH(detailRef.current.scrollHeight);
    } else setMaxH(0);
  },[open, item]);

  useEffect(()=>{
    if(ripples.length){
      const t = setTimeout(()=> setRipples(r=>r.slice(1)), 500);
      return ()=>clearTimeout(t);
    }
  },[ripples]);

  function handleClick(e){
    const rect = e.currentTarget.getBoundingClientRect();
    setRipples(r=>[...r,{
      id: Date.now(),
      x: e.clientX - rect.left,
      y: e.clientY - rect.top
    }]);
    onToggle();
  }

  return (
    <div className="row-wrap">
      <div
        className={"row"+(open?' is-open':'')}
        onClick={handleClick}
        role="button"
        tabIndex={0}
        onKeyDown={e=> e.key==='Enter' && handleClick(e)}
      >
        <div style={{position:'absolute',inset:0,overflow:'hidden'}}>
          {ripples.map(r=>(
            <span key={r.id} className="ripple" style={{left:r.x, top:r.y}} />
          ))}
        </div>

        {/* คอลัมน์ 1: Project + Account */}
        <div className="cell">
          <div className="title-project" title={item.projectname}>{item.projectname}</div>
          <div className="text-account" title={item.accountname}>{item.accountname}</div>
        </div>

        {/* คอลัมน์ 2: วันที่ (ช่วง) */}
        <div className="cell cell-meta">
          <span className="date-text">
            {formatDate(item.dateStart)}
            {item.dateEnd && item.dateEnd !== item.dateStart
              ? ' - ' + formatDate(item.dateEnd)
              : ''}
          </span>
        </div>

        {/* คอลัมน์ 3: เวลา หรือ สถานะ */}
        <div className="cell cell-meta">
          {activeTab==='complete'
            ? <StatusBadge status={item.isStatus} />
            : (item.isStatus && item.isStatus.toLowerCase().includes('schedule')
                ? <StatusBadge status={item.isStatus} />
                : <span>{timeRange(item.timeStart, item.timeEnd)}</span>
              )
          }
        </div>
      </div>

      <div
        className={"detail-anim"+(open?' open':'')}
        style={{maxHeight:open?maxH:0}}
        aria-hidden={!open}
      >
        <div ref={detailRef} className="detail">
          <div className="detail-box">
            <div className="stack">
              <div className="kv"><b>Project ID:</b> {item.projectid}</div>
              <div className="kv"><b>Account:</b> {item.accountname || '-'}</div>
              <div className="kv"><b>Engineers:</b> {item.engineers || '-'}</div>
              <div className="kv"><b>Date Range:</b> {formatDate(item.dateStart)}{item.dateEnd && item.dateEnd!==item.dateStart ? ' - '+formatDate(item.dateEnd):''}</div>
              <div className="kv"><b>Time:</b> {timeRange(item.timeStart, item.timeEnd) || '-'}</div>
              <div className="kv"><b>Status:</b> {item.isStatus}</div>
              {item.dateSuccess && <div className="kv"><b>Completed:</b> {formatDate(item.dateSuccess)}</div>}
            </div>
            <div className="btns">
              {activeTab!=='complete' && (
                <>
                  <button className="btn btn-success">Complete</button>
                  <button className="btn btn-warn">Postpone</button>
                </>
              )}
              {activeTab==='complete' && (
                <button className="btn btn-success">OK</button>
              )}
            </div>
          </div>
        </div>
      </div>

    </div>
  );
}
